import React, { Component } from 'react';

class RegistrationForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      phone: '',
      gender: 'Чоловік',
      error: null,
    };
  }

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  handleSubmit = (event) => {
    event.preventDefault();
    // Тут ви можете додати логіку перевірки введених даних

    if (this.state.name === '', this.state.email === '' , this.state.phone === '') {
      this.setState({ error: 'Заповніть усі обовязкові поля' });
      return;
    }

    // Додавання даних до таблиці або відправка на сервер
    // Очищення форми
    this.props.onSubmit(this.state);
    this.setState({
      name: '',
      email: '',
      phone: '',
      gender: 'Чоловік',
      error: null,
    });
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>Ім'я:
            <input
              type="text"
              name="name"
              value={this.state.name}
              onChange={this.handleInputChange}
              required
            />
          </label>

          <label>Пошта:
            <input
              type="email"
              name="email"
              value={this.state.email}
              onChange={this.handleInputChange}
              required
            />
          </label>

          <label>Номер телефону:
            <input
              type="text"
              name="phone"
              value={this.state.phone}
              onChange={this.handleInputChange}
              required
            />
          </label>

          <label>Стать:
            <input
              type="radio"
              name="gender"
              value="Чоловік"
              checked={this.state.gender === 'Чоловік'}
              onChange={this.handleInputChange}
            />
            Чоловік
            <input
              type="radio"
              name="gender"
              value="Жінка"
              checked={this.state.gender === 'Жінка'}
              onChange={this.handleInputChange}
            />
            Жінка
          </label>

          <button type="submit">Зареєструватися</button>
        </form>

        {this.state.error && <div className="error-message">{this.state.error}</div>}
      </div>
    );
  }
}

export default RegistrationForm;
