import React, { Component } from 'react';

class UserTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedRows: [],
    };
  }

  handleCheckboxChange = (event, id) => {
    const isChecked = event.target.checked;
    if (isChecked) {
      this.setState((prevState) => ({
        selectedRows: [...prevState.selectedRows, id],
      }));
    } else {
      this.setState((prevState) => ({
        selectedRows: prevState.selectedRows.filter((rowId) => rowId !== id),
      }));
    }
  }

  handleDeleteSelectedRows = () => {
    // Видалення вибраних рядків
    this.props.onDeleteRows(this.state.selectedRows);
    this.setState({ selectedRows: [] });
  }

  handleDuplicateSelectedRows = () => {
    // Дублювання вибраних рядків
    this.props.onDuplicateRows(this.state.selectedRows);
    this.setState({ selectedRows: [] });
  }

  render() {
    const { data } = this.props;

    return (
      <div>
        <table>
          <thead>
            <tr>
              <th>Ім'я</th>
              <th>Пошта</th>
              <th>Номер телефону</th>
              <th>Стать</th>
              <th>Дії</th>
            </tr>
          </thead>
          <tbody>
            {data.map((user) => (
              <tr key={user.id}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.phone}</td>
                <td>{user.gender}</td>
                <td>
                  <input
                    type="checkbox"
                    onChange={(e) => this.handleCheckboxChange(e, user.id)}
                    checked={this.state.selectedRows.includes(user.id)}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div>
          <button onClick={this.handleDeleteSelectedRows}>Видалити вибрані</button>
          <button onClick={this.handleDuplicateSelectedRows}>Дублювати вибрані</button>
        </div>
      </div>
    );
  }
}

export default UserTable;
